import React, { FC, ReactElement, useRef, useEffect, useState, ChangeEvent } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { CreateNoteDto, Client, NoteLookupDto, NoteDetailsVm } from '../api/api';
import { FormControl } from 'react-bootstrap';
import { create } from 'domain';
import imageSrc from '../../src/pictures/DropList.png';
import Info from './NoteDetails';
import '../App.css';

const apiClient = new Client('https://localhost:44397');

const Dropdown = () => {
  const [isOpen, setIsOpen] = useState(false);
	const [notes, setNotes] = useState<NoteLookupDto[] | undefined>(undefined);

  const handleToggle = () => {
    setIsOpen(!isOpen);
		console.log('Dropdown toggled:', !isOpen);
  };

  async function getNotes() {
      const noteListVm = await apiClient.getAll('1.0');
      setNotes(noteListVm.notes);
  }

  useEffect(() => {
      setTimeout(getNotes, 500);
  }, []);
	
	const [currentNote, setNote] = useState<NoteDetailsVm>();
	async function getNote( someProp: string | undefined) {
				const NoteDetailsVm = await apiClient.get(someProp || '', '1.0');
				console.log('Received NoteDetailsVm:', NoteDetailsVm);
				setNote(NoteDetailsVm);
	}

	return (
		<div>
			<div className="drop-list">
				<img src={imageSrc} alt="Картинка" className="drop-list-img" onClick={handleToggle} />
				{isOpen && (
					<div>
						{notes?.map(note => (
							<button
							  onClick={() => {
							    console.log('Clicked note:', note);
									  // getNote(note.id);
							  }}
							>
							{note?.title}<Info someProp={note} /></button>
						))}
					</div>
				)}
			</div>

			{/* <div className="container">
				<div className="details">ID: {currentNote?.id}</div>
	      <div className="details">Title: {currentNote?.title}</div>
	      <div className="details">Details: {currentNote?.details}</div>
	      <div className="details">Creation Date: {currentNote?.creationDate?.toLocaleString()}</div>
	      <div className="details">Edit Date: {currentNote?.editDate?.toLocaleString()}</div>
	    </div>   */}
		</div>
	);
};

export default Dropdown;
