import React, { FC, ReactElement, useRef, useEffect, useState, ChangeEvent } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { CreateNoteDto, Client, NoteLookupDto, NoteDetailsVm } from '../api/api';
import { FormControl } from 'react-bootstrap';
import { create } from 'domain';
import '../App.css';

const Info = ({ someProp }: { someProp: string }) => {
  const [isOpen, setIsOpen] = useState(false);
	const [note, setNote] = useState<NoteDetailsVm>();

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

const apiClient = new Client('https://localhost:44397');

async function createNote(note: CreateNoteDto) {
    await apiClient.create('1.0', note);
    console.log('Note is created.');
}

async function getNote() {
  if (note !== undefined) { 
    if (note.id !== undefined) {
      const NoteDetailsVm = await apiClient.get(someProp || '', '1.0');
      setNote(NoteDetailsVm);
    }
  }
}

return (
  <div>
    <div>
      {note?.title}
    </div>
    <div>
      {note?.details}
    </div>
    <div>
      {note?.creationDate?.toLocaleDateString()}
    </div>  
  </div>
);
};

export default Info;
