import React, { FC, ReactElement, useRef, useEffect, useState, ChangeEvent } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { CreateNoteDto, Client, NoteLookupDto, NoteDetailsVm, UpdateNoteDto } from '../api/api';
import { FormControl } from 'react-bootstrap';
import { create } from 'domain';
import '../App.css';
import {IoCloseCircleSharp, IoHammerSharp} from "react-icons/io5";
import Delete from './NotesDelete';

const apiClient = new Client('https://localhost:44397');

const NoteEdit = ({ someProp }: { someProp: string | undefined}) => {

    console.log('Calling Info');

    const [editedNote, setEditedNote] = useState<UpdateNoteDto>();
    const [newTitle, setNewTitle] = useState<string>();
    const [newDetails, setNewDetails] = useState<string>();
  
    async function editNote() {
        setEditedNote({
            id: someProp,
            title: newTitle,
            details: newDetails
        });
        console.log('PREPARE UpdateNoteDto:', editedNote);
        // const NoteDetailsVm = await apiClient.get(someProp || '', '1.0');
        const NoteDetailsVm = await apiClient.update('1.0', editedNote);
        console.log('EDITED NoteDetailsVm:', NoteDetailsVm);
    }

    useEffect(() => {
        console.log('Calling getNote()');
    });
  
    return (
        <div>
            <div className="note-edit">
                <input className="details" placeholder="Title" onChange={(e) => setNewTitle(e.target.value)} />
                <textarea className="details" placeholder="Details" onChange={(e) => setNewDetails(e.target.value)} />
                <button className="details" type="button" onClick={editNote}>Изменить</button>
                {/* <h3 className="details">Title: {currentNote?.title}</h3> */}
                {/* <div className="details">Details: {currentNote?.details}</div> */}
            </div> 
        </div>
    );
};

export default NoteEdit;
