import React, { FC, ReactElement, useRef, useEffect, useState, ChangeEvent } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { CreateNoteDto, Client, NoteLookupDto, NoteDetailsVm } from '../api/api';
import { FormControl } from 'react-bootstrap';
import { create } from 'domain';
import '../App.css';
import {IoCloseCircleSharp, IoHammerSharp} from "react-icons/io5";
import Delete from './NotesDelete';
import NoteEdit from './NoteEdit';

const apiClient = new Client('https://localhost:44397');

const NoteDetails = ({ someProp, setIsOpen, isOpen}: 
    { someProp: NoteLookupDto | undefined; 
      setIsOpen: (isOpen: boolean) => void;
      isOpen: boolean }) => {

    console.log('Calling Info');

    const [currentNote, setNote] = useState<NoteDetailsVm>();
    const [isEdit, setIsEdit] = useState(false);
  
    async function getNote( someProp: string | undefined) {
        const NoteDetailsVm = await apiClient.get(someProp || '', '1.0');
        console.log('Received NoteDetailsVm:', NoteDetailsVm);
        setNote(NoteDetailsVm);
    }

    useEffect(() => {
        console.log('Calling getNote()');
        getNote(someProp?.id);
    });

    console.log('Rendering Info component');

    const handleDelete = ( note: string | undefined) => {
		Delete({ someProp: note });
		// setTimeout(getNotes, 500);
	};

	const handleToggle = () => {
        setIsEdit(!isEdit);
		console.log('Dropdown toggled:', !isOpen);
	};
  
    return (
        <div>
            <div className="note-details">
                <IoCloseCircleSharp className="delete-icon" 
                    onClick={() => { setIsOpen(!isOpen); handleDelete(someProp?.id);}} />
                <IoHammerSharp className="edit-icon" onClick={handleToggle} />

		  	    <div className="details">ID: {currentNote?.id}</div>
                <h3 className="details">Title: {currentNote?.title}</h3>
                <div className="details">Details: {currentNote?.details}</div>
                <span className="creation-date">Creation Date: {currentNote?.creationDate?.toLocaleString()}</span>
                <span className="edit-date">Edit Date: {currentNote?.editDate?.toLocaleString()}</span>
            </div> 

            <div>
                {isEdit && (
                    < NoteEdit someProp={someProp?.id} />
                )}
            </div>
        </div>
    );
};

export default NoteDetails;
