import React, { FC, ReactElement, useRef, useEffect, useState, ChangeEvent } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { CreateNoteDto, Client, NoteLookupDto, NoteDetailsVm } from '../api/api';
import { FormControl } from 'react-bootstrap';
import { create } from 'domain';
import imageSrc from '../../src/pictures/DropList.png';
import imageTrash from '../../src/pictures/trash.png'
import Info from './NoteDetails';
import '../App.css';
import Delete from './NotesDelete';
import NoteDetails from './NoteDetails';
import {IoCloseCircleSharp, IoHammerSharp} from "react-icons/io5";

const apiClient = new Client('https://localhost:44397');

const Note = ({ someProp }: { someProp: NoteLookupDto | undefined}) => {
	const [isOpen, setIsOpen] = useState(false);
	const [notes, setNotes] = useState<NoteLookupDto[] | undefined>(undefined);
	const [clickedNote, setLookNote] = useState<NoteLookupDto>();

	const handleToggle = () => {
		setIsOpen(!isOpen);
		console.log('Dropdown toggled:', !isOpen);
	};

	async function getNotes() {
    	const noteListVm = await apiClient.getAll('1.0');
		console.log('Getting Notes!');
    	setNotes(noteListVm.notes);
	}

	useEffect(() => {
    	setTimeout(getNotes, 500);
	});

	const handleClick = ( note: NoteLookupDto) => {
		console.log('Clicked NoteDetailsVm:', note);
		setLookNote(note);
	};

	const handleDelete = ( note: string | undefined) => {
		Delete({ someProp: note });
		// setTimeout(getNotes, 500);
	};

    const handleNote = () => {
		setIsOpen(!isOpen);
		console.log('Dropdown toggled:', !isOpen);
	};

	return (
		<div>
            <div className="Action">
                <div className="Note" onClick={handleToggle}>
                    <h3>{someProp?.title}</h3>
                </div>
                <div>
                    <IoCloseCircleSharp className="delete-icon" 
                        onClick={() => { handleDelete(someProp?.id);}} />
                    <IoHammerSharp className="edit-icon"/>
                </div>
            </div>


            <div>
                {isOpen && (
                    < NoteDetails someProp={someProp}/>
                )}
            </div>
		</div>
	);
};

export default Note;
