import React, { FC, ReactElement, useRef, useEffect, useState, ChangeEvent } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { CreateNoteDto, Client, NoteLookupDto, NoteDetailsVm } from '../api/api';
import { FormControl } from 'react-bootstrap';
import { create } from 'domain';
import '../App.css';
import {AiFillCloseSquare} from 'react-icons/ai'
import {IoCloseCircleSharp, IoHammerSharp} from "react-icons/io5";
import DeleteNote from './NoteDelete';

const apiClient = new Client('https://localhost:44397');

const Note = ({ note, getNotes, setNotes }: 
    {note: NoteLookupDto | undefined; getNotes: () => void; 
    setNotes: React.Dispatch<React.SetStateAction<NoteLookupDto[] | undefined>> 
}) => {

	const handleDelete = ( note: string | undefined) => {
		DeleteNote({ someProp: note });
        getNotes();
	};

    return(
        <div className="container">
            <div className="note">
                <h3>{note?.title}</h3>
            </div>
            <div>
                <div className="delete-icon">
                    <IoCloseCircleSharp className="icon" onClick={() => handleDelete(note?.id)}/>
                </div>
                <div className="edit-icon">
                    <IoHammerSharp className="icon"/>
                </div>
            </div>
        </div>
    );
};

export default Note;
