import React, { FC, ReactElement, useRef, useEffect, useState, ChangeEvent } from 'react';
import { CreateNoteDto, Client, NoteLookupDto } from '../api/api';
import { FormControl } from 'react-bootstrap';
import { create } from 'domain';
import imageSrc from '../../src/pictures/DropList.png'
import '../App.css';

const Dropdown = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedValue, setSelectedValue] = useState('');
	let textInput = useRef(null);
	const [notes, setNotes] = useState<NoteLookupDto[] | undefined>(undefined);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleChange = (event: ChangeEvent<HTMLSelectElement>) => {
    setSelectedValue(event.target.value);
  };

const apiClient = new Client('https://localhost:44397');

async function createNote(note: CreateNoteDto) {
    await apiClient.create('1.0', note);
    console.log('Note is created.');
}

    async function getNotes() {
        const noteListVm = await apiClient.getAll('1.0');
        setNotes(noteListVm.notes);
    }

    useEffect(() => {
        setTimeout(getNotes, 500);
    }, []);

    const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            const note: CreateNoteDto = {
                title: event.currentTarget.value,
                details: ""
            };
            createNote(note);
            event.currentTarget.value = '';
            setTimeout(getNotes, 500);
        }
    };

return (
	<div className="drop-list">
		<img src={imageSrc} alt="Картинка" className="drop-list-img" onClick={handleToggle} />
		{isOpen && (
			<div>
				{notes?.map(note => (
					<div>{note.title}</div>
				))}
			</div>
		)}
	</div>
);
};

export default Dropdown;
